import React, { useState } from 'react'

function Search() {

  const myApi = "AIzaSyCeUYY_NIfrVtx58mHRhKh5elskAZOkSjQ";
  const [bookName, setBookName] = useState("react");
  const [data, setData] = useState();

  const fetchData = async () => {
    await fetch(`https://www.googleapis.com/books/v1/volumes?q=${bookName}&key=${myApi}`).then((response) => {
      response.json().then(res => setData(res.items));
    })
  }

  const searchBook = () => {
    fetchData();
  }

  return (
    <div className='flex flex-col justify-center items-center overflow-y-auto'>
      <div className='upperBox flex flex-col justify-center items-center'>
        <input className='mb-5 text-black font-bold border-black rounded-2xl p-3' placeholder="Enter Book Name" onChange={(e) => setBookName(e.target.value)} />
        <button className='bg-white font-bold text-black w-fit p-2 rounded-xl hover:scale-105 ' onClick={searchBook}>Search</button>
      </div>
      <div className='lowerBox flex flex-wrap '>
        {
          data?.map(d => {
            return (
              <div className='box flex flex-col justify-center items-center m-5 border border-slate-200'>
                <img src={d.volumeInfo.imageLinks?.thumbnail} className="object-fit m-5 " />
                <span>{d.volumeInfo.title} </span>
              </div>
            )
          })
        }

      </div>
    </div>
  )
}

export default Search
