import React, { useEffect, useState } from 'react'

function Search() {

  const myApi = "AIzaSyCeUYY_NIfrVtx58mHRhKh5elskAZOkSjQ";
  const [bookName, setBookName] = useState("react");

  const fetchData = async () => {
    const data = await fetch(`https://www.googleapis.com/books/v1/volumes?q=${bookName}&key=${myApi}`).then((response) => {
      console.log(response.json())
    })
  }

  const searchBook = () => {
    fetchData();
  }

  // useEffect(() => {
    // fetchData();
  // }, []);

  return (
    <div className='flex flex-col justify-center items-center'>
      <div className='upperBox flex flex-col justify-center items-center'>
        <input className='mb-5 text-black font-bold border-black rounded-2xl p-3' placeholder="Enter Book Name" onChange={(e) => setBookName(e.target.value)} />
        <button className='bg-white font-bold text-black w-fit p-2 rounded-xl hover:scale-105 ' onClick={searchBook}>Search</button>
      </div>
      <div className='lowerBox'></div>
    </div>
  )
}

export default Search
