const mongoose = require("mongoose");
mongoose.connect("mongodb://localhost:27017/booky");

const bookySchema = new mongoose.Schema({
    userName: String,
    userId: String,
    searchKeyword: String,
    bookId: String,
    bookName: String,
    date: {
        type: Date,
        default: Date.now
    }
});

const booky = new mongoose.model("bookyCollection", bookySchema);
export default addNewDocument = (userName, userId, bookName) => {
    const firstDocument = new booky({
        userName: userName,
        userId: userId,
        bookName: bookName,
    });
    firstDocument.save();
}


// booky present in the url is indicating the database name
// bookyCollection written in the previous line is indicating the collection


// database
// collection {we need to define the schema of documents}
// documents
